        <div className="report-list">
          <ul className="nav nav-tabs ">
            {tabMenus.map((menu) => (
              <li className="nav-link" key={menu.id}>
                <a href="#" className="nav-link">
                  {menu.name}
                </a>
              </li>
            ))}
          </ul>
          <hr className="m-0" />

          <div className="reports-list">
