import FrontLayout from "@/layouts/FrontLayout";
import React from "react";
import { useState } from "react";
import styled from "styled-components";

const ReportsListPage = () => {
  const [activeTab, setActiveTab] = useState("All");
  return (
    <FrontLayout>
      <Wrapper>
        <div className="reports">
          <div className="container">
            <div className="reports-search">
              <input
                type="search"
                name="reports-search-input"
                className="form-control"
                placeholder="Search reports"
              />
              <i className="fas fa-search c-hand"></i>
            </div>

            <div className="reports-tab mt-4">
              <ul className="nav nav-tabs">
                {tabList.map((tab, i) => (
                  <li className="nav-item" key={i}>
                    <a
                      href="#"
                      className={`nav-link ${
                        activeTab === tab ? "active" : ""
                      }`}
                      onClick={() => setActiveTab(tab)}
                    >
                      {tab}
                    </a>
                  </li>
                ))}
              </ul>
            </div>

            <div className="reports-map">
              <div className="wrapper">
                <div className="wrapper-header">
                  <h6 className="mt-3 mb-0">OGUNSANYA OLUWASEYI V THE STATE</h6>
                  <div className="small">
                    <small>Supprem Court</small>
                    <small>HELA-11323-vol-2-1125</small>
                  </div>
                </div>

                <p>
                  Adipiscing luctus potenti nunc etiam mauris in mollis
                  tristique. Nibh integer iaculis vehicula interdum tristique
                  lectus magna. Sodales malesuada vel est metus. Faucibus
                  pretium pulvinar. Adipiscing luctus potenti nunc etiam mauris
                  in mollis tristique. Nibh integer iaculis vehicula interdum
                  tristique lectus magna. Sodales malesuada vel est metus.
                  Faucibus pretium pulvinar.
                </p>
              </div>
            </div>
          </div>
        </div>
      </Wrapper>
    </FrontLayout>
  );
};

const Wrapper = styled.div`
  .reports {
    &-search {
      display: flex;
      align-items: center;
      width: 100%;
      max-width: 768px;
      margin: auto;
      input {
        margin-right: 1rem;
      }
      .fa-search {
        font-size: 1.5rem;
      }
    }

    &-tab {
      .nav-tabs {
        .nav-link {
          transition: 0.5s all ease-in-out;
          &.active {
            border-bottom: 2px solid red;
          }
        }
      }
    }
  }
`;

export default ReportsListPage;

const tabList = [" All", "Suppreme Court", "Court of Appeal"];
