        <div className="report-tab">
          <ul className="nav  ">
            {tabMenus.map((menu) => (
              <li
                className="nav-item report-tab-item"
                key={menu.id}
                onClick={() => setTabIndex(menu.id)}
              >
                <a
                  className={`nav-link c-hand report-tab-item ${
                    tabIndex === menu.id ? "active" : ""
                  }`}
                >
                  {menu.name}
                </a>
